    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed

        double coefA = Double.parseDouble(txtA.getText());
        double coefB = Double.parseDouble(txtB.getText());
        double coefC = Double.parseDouble(txtC.getText());
        double x1, x2;
        // proceso
        x1 = (-coefB + Math.sqrt((Math.pow(coefB, 2)) - (4 * coefA * coefC))) / (2 * coefA);
        x2 = (-coefB - Math.sqrt((Math.pow(coefB, 2)) - (4 * coefA * coefC))) / (2 * coefA);
        // salida
        txtSol.setText("x1 = " + Math.round(x1) + ", x2 = " + Math.round(x2));
    }//GEN-LAST:event_btnCalcularActionPerformed

