    private void btnCalcularCirculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularCirculoActionPerformed

        double radio = Double.parseDouble(txtRadio.getText());
        txtAreaCirculo.setText("" + Math.round(Math.PI * Math.pow(radio, 2)));
        txtPerimetroCirculo.setText("" + Math.round(2 * Math.PI * radio));

    }//GEN-LAST:event_btnCalcularCirculoActionPerformed

    private void btnBorrarCirculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarCirculoActionPerformed
        txtRadio.setText("");
        txtAreaCirculo.setText("");
        txtPerimetroCirculo.setText("");
    }//GEN-LAST:event_btnBorrarCirculoActionPerformed

    private void btnCalcularCuadradoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularCuadradoActionPerformed
        double lado = Double.parseDouble(txtLadoCuadrado.getText());
        txtAreaCuadrado.setText("" + lado * lado);
        txtPerimetroCuadrado.setText("" + 4 * lado);
    }//GEN-LAST:event_btnCalcularCuadradoActionPerformed

    private void btnBorrarCuadradoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarCuadradoActionPerformed
        txtLadoCuadrado.setText("");
        txtAreaCuadrado.setText("");
        txtPerimetroCuadrado.setText("");
    }//GEN-LAST:event_btnBorrarCuadradoActionPerformed

    private void btnCalcularRomboActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularRomboActionPerformed
        double diagonal_sup = Double.parseDouble(txtDiagSup.getText());
        double diagonal_inf = Double.parseDouble(txtDiagInf.getText());
        txtAreaRombo.setText("" + (diagonal_sup * diagonal_inf) / 2);
        txtPerimetroRombo.setText("" + Math.round(4 * Math.pow((diagonal_sup * diagonal_sup) / 4 + (diagonal_inf * diagonal_inf) / 4, 0.5)));
    }//GEN-LAST:event_btnCalcularRomboActionPerformed

    private void btnBorrarRomboActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarRomboActionPerformed
        txtDiagSup.setText("");
        txtDiagInf.setText("");
        txtAreaRombo.setText("");
        txtPerimetroRombo.setText("");
    }//GEN-LAST:event_btnBorrarRomboActionPerformed

    private void btnCalcularTrianguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularTrianguloActionPerformed
        double base = Double.parseDouble(txtBaseTriangulo.getText());
        double altura = Double.parseDouble(txtAlturaTriangulo.getText());
        double hipotenusa = Math.round(Math.pow(base * base + altura * altura, 0.5));

        txtAreaTriangulo.setText("" + Math.round((base * altura / 2)));
        txtPerimetroTriangulo.setText("" + (base + altura + hipotenusa));
        txtHipotenusa.setText("" + hipotenusa);

        if ((base == altura) && (base == hipotenusa) && (altura == hipotenusa)) {
            txtTipoTriangulo.setText("Triángulo equilatero");
        } else if ((base != altura) && (base != hipotenusa) && (altura != hipotenusa)) {
            txtTipoTriangulo.setText("Triángulo escaleno");
        } else {
            txtTipoTriangulo.setText("Triángulo isosceles");
        }
    }//GEN-LAST:event_btnCalcularTrianguloActionPerformed

    private void btnBorrarTrianguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBorrarTrianguloActionPerformed
        txtBaseTriangulo.setText("");
        txtAlturaTriangulo.setText("");
        txtAreaTriangulo.setText("");
        txtPerimetroTriangulo.setText("");
        txtHipotenusa.setText("");
        txtTipoTriangulo.setText("");
    }//GEN-LAST:event_btnBorrarTrianguloActionPerformed

