    private void btnCalcularTrapecioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularTrapecioActionPerformed

        double base_menor = Double.parseDouble(txtBaseMenor.getText());
        double base_mayor = Double.parseDouble(txtBaseMayor.getText());
        double altura = Double.parseDouble(txtAlturaTrapecio.getText());
        double lado1 = Double.parseDouble(txtLado1.getText());
        double lado2 = Double.parseDouble(txtLado2.getText());

        txtAreaTrapecio.setText("" + ((base_menor + base_mayor) / 2) * altura);
        txtPerimetroTrapecio.setText("" + (base_menor + base_mayor + lado1 + lado2));
    }//GEN-LAST:event_btnCalcularTrapecioActionPerformed

