    private void btnCalcularRectanguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularRectanguloActionPerformed
        double base = Double.parseDouble(txtBaseRectangulo.getText());
        double altura = Double.parseDouble(txtAlturaRectangulo.getText());

        txtAreaRectangulo.setText("" + (base * altura));
        txtPerimetroRectangulo.setText("" + ((2 * base) + (2 * altura)));
    }//GEN-LAST:event_btnCalcularRectanguloActionPerformed

